sys_script_include.script:
var ViaCEPUtils = Class.create();
ViaCEPUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getCepData: function() {
        var cep = this.getParameter('sysparm_cep');

        try {
            var r = new sn_ws.RESTMessageV2('x_955017_bootcam_0.API ViaCEP', 'get cep');
            r.setStringParameterNoEscape('cep', cep);

            var response = r.execute();
            var responseBody = response.getBody();
            responseBody = JSON.parse(responseBody);
            var httpStatus = response.getStatusCode();

            if (httpStatus == 200) {
                return JSON.stringify({
                    logradouro: responseBody.logradouro,
                    bairro: responseBody.bairro,
                    localidade: responseBody.localidade,
                    uf: responseBody.uf
                });
            }
        } catch (ex) {
            var message = ex.message;
        }
    },
    type: 'ViaCEPUtils'
});
